import React, { useState } from 'react';
import './SignUp.css'; // Importez un fichier CSS pour le style

function SignUp() {
  const [formData, setFormData] = useState({
    email: '',
    dateOfBirth: '',
    phone: '',
    username: '',
    password: '',
    repassword: '',
  });

  const [errors, setErrors] = useState({});
  const [isSuccessful, setIsSuccessful] = useState(false);
  const [isSignUpFailed, setIsSignUpFailed] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const validateForm = () => {
    let formErrors = {};

    if (!formData.email) formErrors.email = 'Champ obligatoire';
    else if (!/\S+@\S+\.\S+/.test(formData.email))
      formErrors.email = "L'e-mail doit être une adresse e-mail valide";

    if (!formData.dateOfBirth) formErrors.dateOfBirth = 'Champ obligatoire';

    if (!formData.phone) formErrors.phone = 'Champ obligatoire';
    else if (formData.phone.length < 8)
      formErrors.phone = 'Le numéro de téléphone doit être composé de 8 chiffres';

    if (!formData.username) formErrors.username = 'Champ obligatoire';
    else if (formData.username.length < 3)
      formErrors.username = "Le nom d'utilisateur doit comporter au moins 3 caractères";
    else if (formData.username.length > 20)
      formErrors.username = "Le nom d'utilisateur doit comporter au maximum 20 caractères";

    if (!formData.password) formErrors.password = 'Champ obligatoire';
    else if (formData.password.length < 6)
      formErrors.password = 'Le mot de passe doit contenir au moins 6 caractères';

    if (!formData.repassword) formErrors.repassword = 'Champ obligatoire';
    else if (formData.repassword !== formData.password)
      formErrors.repassword = 'Les mots de passe saisis ne correspondent pas';

    setErrors(formErrors);
    return Object.keys(formErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      // Simuler une inscription réussie
      setIsSuccessful(true);
      setIsSignUpFailed(false);
      console.log("Données du formulaire: ", formData);
    } else {
      setIsSignUpFailed(true);
      setIsSuccessful(false);
    }
  };

  return (
    <div className="signup-container">
      <h2 className="form-login-heading">inscrivez-vous maintenant</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <div className="col-6">
            <label>E-mail</label>
            <input
              type="email"
              className={`form-control ${errors.email ? 'is-invalid' : ''}`}
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Email"
              required
            />
            {errors.email && <small className="text-danger">{errors.email}</small>}
          </div>

          <div className="col-6">
            <label>Date de naissance</label>
            <input
              type="date"
              className={`form-control ${errors.dateOfBirth ? 'is-invalid' : ''}`}
              name="dateOfBirth"
              value={formData.dateOfBirth}
              onChange={handleChange}
              required
            />
            {errors.dateOfBirth && (
              <small className="text-danger">{errors.dateOfBirth}</small>
            )}
          </div>
        </div>

        <div className="form-row">
          <div className="col-6">
            <label>Téléphone</label>
            <input
              type="tel"
              className={`form-control ${errors.phone ? 'is-invalid' : ''}`}
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              placeholder="phone"
              required
            />
            {errors.phone && <small className="text-danger">{errors.phone}</small>}
          </div>

          <div className="col-6">
            <label>Nom d'utilisateur</label>
            <input
              type="text"
              className={`form-control ${errors.username ? 'is-invalid' : ''}`}
              name="username"
              value={formData.username}
              onChange={handleChange}
              placeholder="username"
              required
            />
            {errors.username && <small className="text-danger">{errors.username}</small>}
          </div>
        </div>

        <div className="form-row">
          <div className="col-6">
            <label>Mot de passe</label>
            <input
              type="password"
              className={`form-control ${errors.password ? 'is-invalid' : ''}`}
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="password"
              required
            />
            {errors.password && <small className="text-danger">{errors.password}</small>}
          </div>

          <div className="col-6">
            <label>Confirmez le mot de passe</label>
            <input
              type="password"
              className={`form-control ${errors.repassword ? 'is-invalid' : ''}`}
              name="repassword"
              value={formData.repassword}
              onChange={handleChange}
              placeholder="password"
              required
            />
            {errors.repassword && (
              <small className="text-danger">{errors.repassword}</small>
            )}
          </div>
        </div>

        <button type="submit" className="btn btn-theme btn-block">
          <i className="fa fa-lock"></i> S'inscrire
        </button>

        {isSignUpFailed && (
          <div className="alert alert-warning">L'inscription a échoué !</div>
        )}
        {isSuccessful && (
          <div className="success">vous vous êtes inscrit avec succès</div>
        )}
      </form>
    </div>
  );
}

export default SignUp;
