import React, { useState } from 'react';
import './Login.css';

const LoginForm = () => {
  const [form, setForm] = useState({ username: '', password: '' });
  const [errors, setErrors] = useState({ username: '', password: '' });
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isLoginFailed, setIsLoginFailed] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const validate = () => {
    const newErrors = { username: '', password: '' };
    if (!form.username) {
      newErrors.username = "Le nom d'utilisateur est obligatoire";
    } else if (form.username.length < 3) {
      newErrors.username = "Le nom d'utilisateur doit comporter au moins 3 caractères";
    } else if (form.username.length > 20) {
      newErrors.username = "Le nom d'utilisateur doit comporter au maximum 20 caractères";
    }

    if (!form.password) {
      newErrors.password = "Le mot de passe est obligatoire";
    } else if (form.password.length < 6) {
      newErrors.password = "Le mot de passe doit contenir au moins 6 caractères";
    }

    setErrors(newErrors);
    return Object.values(newErrors).every(error => error === '');
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    if (validate()) {
      // Simuler la logique de connexion
      // Vous pouvez remplacer ce code par un appel d'API
      if (form.username === "admin" && form.password === "admin") {
        setIsLoggedIn(true);
        setIsLoginFailed(false);
        setErrorMessage('');
      } else {
        setIsLoginFailed(true);
        setErrorMessage("Identifiants incorrects");
      }
    } else {
      setIsLoginFailed(true);
    }
  };

  return (
    <form className="login-form" onSubmit={onSubmit}>
  <div className="card-container">
    <h2 className="login-heading">Connectez-vous maintenant</h2>
    
    {!isLoggedIn && (
      <div className="login-wrapper">
        <div>
          <input
            type="text"
            className={`input-field ${errors.username ? 'input-error' : ''}`}
            name="username"
            value={form.username}
            onChange={handleChange}
            placeholder="Nom d'utilisateur"
            required
            autoFocus
          />
          <div>
            {errors.username && (
              <small className="error-message" style={{ fontSize: '11px' }}>{errors.username}</small>
            )}
          </div>
        </div>
        <br />
        <div>
          <input
            type="password"
            className={`input-field ${errors.password ? 'input-error' : ''}`}
            name="password"
            value={form.password}
            onChange={handleChange}
            placeholder="Mot de passe"
            required
          />
          <div>
            {errors.password && (
              <small className="error-message" style={{ fontSize: '11px' }}>{errors.password}</small>
            )}
          </div>
        </div>
        <div>
          <label className="checkbox-container">
            <input type="checkbox" value="remember-me" />
            <span className="checkbox-label"><h3 className="small-heading">Remember me</h3></span>
          </label>
        </div>
        <div>
          <button className="submit-button button button-block" type="submit">
            <i className="fa fa-lock"></i> Connexion
          </button>
        </div>
        <hr />
        <div className="form-group">
          <div className="alert" role="alert" style={{ display: isLoginFailed ? 'block' : 'none' }}>
            Échec de la connexion : {errorMessage}
          </div>
        </div>
      </div>
    )}

    {isLoggedIn && <div className="success-message">Connexion réussie !</div>}
  </div>
</form>
  );
};

export default LoginForm;
