import React, { useState } from 'react';
import './Login.css';

const LoginForm = () => {
  const [form, setForm] = useState({ username: '', password: '' });
  const [errors, setErrors] = useState({});
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isLoginFailed, setIsLoginFailed] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const validate = () => {
    const newErrors = {};
    if (!form.username) newErrors.username = "Le nom d'utilisateur est obligatoire";
    else if (form.username.length < 3) newErrors.username = "Le nom d'utilisateur doit comporter au moins 3 caractères";
    else if (form.username.length > 20) newErrors.username = "Le nom d'utilisateur doit comporter au maximum 20 caractères";

    if (!form.password) newErrors.password = "Le mot de passe est obligatoire";
    else if (form.password.length < 6) newErrors.password = "Le mot de passe doit contenir au moins 6 caractères";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    if (validate()) {
      // Logic de connexion, appel API
      setIsLoggedIn(true);
      setIsLoginFailed(false);
    } else {
      setIsLoginFailed(true);
    }
  };

  return (
    <form className="form-login" onSubmit={onSubmit}>
      <div className="card-container">
        <h2 className="form-login-heading">Connectez-vous maintenant</h2>

        {!isLoggedIn && (
          <div className="login-wrap">
            <div className="form-group">
              <input
                type="text"
                className={`form-control ${errors.username ? 'is-invalid' : ''}`}
                name="username"
                value={form.username}
                onChange={handleChange}
                placeholder="Nom d'utilisateur"
                required
              />
              <small className="text-danger">{errors.username}</small>
            </div>

            <div className="form-group">
              <input
                type="password"
                className={`form-control ${errors.password ? 'is-invalid' : ''}`}
                name="password"
                value={form.password}
                onChange={handleChange}
                placeholder="Mot de passe"
                required
              />
              <small className="text-danger">{errors.password}</small>
            </div>

            <div className="form-group">
              <label className="checkbox">
                <input type="checkbox" value="remember-me" /> <h3>Remember-me</h3>
              </label>
            </div>

            <button className="btn btn-theme btn-block" type="submit">
              <i className="fa fa-lock"></i> Connexion
            </button>

            {isLoginFailed && (
              <div className="alert alert-danger">
                Échec de la connexion : {errorMessage}
              </div>
            )}
          </div>
        )}

        {isLoggedIn && <div className="success">Connexion réussie !</div>}
      </div>
    </form>
  );
};

export default LoginForm;
